import { useReducer } from 'react';
import CartContext from './cart-context'

const cartReducer = (state,action) => {
    if(action.type === 'ADD_CART_ITEM') {
        const updatedItems = state.items.concat(action.item) 
        //unlike push, concat adds it to a new array, so does not add it to old 'state' snapshot
        console.log(updatedItems);
        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;
        return {
            items: updatedItems,
            totalAmount: updatedTotalAmount
        };
    }

    return defaultCartState;
};

const defaultCartState = {
    items: [],
    totalAmount: 0,
}

const CartProvider = props => {
    const [carState,dispatchCartAction] = useReducer(cartReducer,defaultCartState);

    const addItemCartHandler = item => {
        dispatchCartAction({
            type: 'ADD_CART_ITEM',
            item: item
        });
    };

    const removeItemFromCartHandler = id => {
        dispatchCartAction({
            type: 'REMOVE_CART_ITEM',
            id: id
        });
    };

    const cartContext = {
        items: carState.items,
        totalAmount: carState.totalAmount,
        addItem: addItemCartHandler,
        removeItem: removeItemFromCartHandler
    };
    return <CartContext.Provider value={cartContext}>
        {props.children}
    </CartContext.Provider>
};

export default CartProvider;
